    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
/////////////DECIMALES
    DecimalFormat decimales = new DecimalFormat("0.00");

        // Declarando Variables: 
        String nn=txt1.getText();
        if (nn.equals("")) {
            JOptionPane.showMessageDialog(rootPane, "Ingrese un DÃ­gito","LAPSUS", JOptionPane.DEFAULT_OPTION);
        }else{
        double n1;
        n1=Double.parseDouble(txt1.getText());
        int op=cmb1.getSelectedIndex();
     
        //Con Gramos
        if (op==0) {
            double oz=ozAgramo(n1);
            txt2.setText(decimales.format(oz));
        }
        if (op==1) {
            double lb=lbAgramo(n1);
            txt2.setText(decimales.format(lb));
        }
        if (op==2) {
            double kg=kgAgramo(n1);
            txt2.setText(decimales.format(kg));
        }
     
        //Segundo COMBOBOX
        int op2=cmb2.getSelectedIndex();
            
            //Con kg
            if (op2==1 && op==0) {
                double oz=ozAkg(n1);
                txt2.setText(decimales.format(oz));
            }
            if (op2==1 && op==1) {
                double lb=lbAkg(n1);
                txt2.setText(decimales.format(lb));
            }
            if (op2==1 && op==3) {
                double gr=grAkg(n1);
                txt2.setText(decimales.format(gr));
            }
        
            //Con Onzas
            if (op==1 && op2==2) {
                double lb=lbAoz(n1);
                txt2.setText(decimales.format(lb));
            }
            if (op==2 && op2==2) {
                double kg=kgAoz(n1);
                txt2.setText(decimales.format(kg));
            }
            if (op==3 && op2==2) {
                double gr=grAoz(n1);
                txt2.setText(decimales.format(gr));
            }
            
            //Con libras
            if (op==0 && op2==3) {
                double oz=ozAlb(n1);
                txt2.setText(decimales.format(oz));
            }
            if (op==2 && op2==3) {
                double kg=kgAlb(n1);
                txt2.setText(decimales.format(kg));
            }
            if(op==3 && op2==3){
                double gr=grAlb(n1);
                txt2.setText(decimales.format(gr));
            }
        }
        
//Iniciar en Caja de Texto1
        txt1.requestFocus();        
    }//GEN-LAST:event_jButton1ActionPerformed
    
    ////////////////Operaciones en Gramos
    public static double kgAgramo(double n1){
        double gramo;
        gramo=n1*1000;
        return gramo;    
    }
    
    public static double ozAgramo(double n1){
        double gramo;
        gramo=n1/0.035274;
        return gramo;
    }
    
    public static double lbAgramo(double n1){
        double gramo;
        gramo=n1/0.0022046;
        return gramo;
    }
    //////////////Operaciones en Kg
    public static double ozAkg(double n1){
        double kg;
        kg=n1/35.274;
        return kg;
    }
    public static double lbAkg(double n1){
        double kg;
        kg=n1/2.2046;
        return kg;
    }
    public static double grAkg(double n1){
        double kg;
        kg=n1/1000;
        return kg;
    }
   //////////////Operaciones en Onzas
    public static double lbAoz(double n1){
        double oz;
        oz=n1*16;
        return oz;
    }
    public static double kgAoz(double n1){
        double oz;
        oz=n1*35.274;
        return oz;
    }
    public static double grAoz(double n1){
        double oz;
        oz=n1*0.035274;
        return oz;
    }
    /////////////Operaciones en libras
    public static double ozAlb(double n1){
        double lb;
        lb=n1/0.0625;
        return lb;
    }
    public static double kgAlb(double n1){
        double lb;
        lb=n1*2.2046;
        return lb;
    }
    public static double grAlb(double n1){
        double lb;
        lb=n1*0.0022046;
        return lb;
    }
    private void txt1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt1KeyTyped
        // TODO add your handling code here:
        
        char x;
        x=evt.getKeyChar();
        if (Character.isLetter(x) || x==',') {
            getToolkit().beep();
            evt.consume();
        }
        
    }//GEN-LAST:event_txt1KeyTyped

    private void txt2KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt2KeyTyped
        // TODO add your handling code here:
        char x=evt.getKeyChar();
        if (x<'0' || x>'9') {
            getToolkit().beep();
            evt.consume();
        }
    }//GEN-LAST:event_txt2KeyTyped

